import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { callApi } from "../api";

const AccountForm = ({ action, setToken, setUserData }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const isLogin = action === "login";
  const title = isLogin ? "Login" : "Register";
  const oppositeTitle = isLogin ? "Register" : "Login";
  const oppositeAction = isLogin ? "register" : "login";
  const actionURL = isLogin ? API_LOGIN : API_REGISTER;
  const history = useHistory();

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log(`user name: ${username}`);
    console.log(`password: ${password}`);
    try {
      const response = await fetch(actionURL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user: {
            username: username,
            password: password,
          },
        }),
      });

      const { data } = await response.json();
      // console.log(data);
      const token = data.token;
      // console.log(token);
      if (token) {
        setToken(token);
        const userResponse = await fetch(API_USER, {
          headers: {
            "Content-type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });
        const { data } = await userResponse.json();
        //console.log(data);
        setUserData(data);
        history.push("/");
      }
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <>
      <h4>{title}</h4>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="username"
          value={username}
          onChange={(event) => setUsername(event.target.value)}
        ></input>
        <input
          type="password"
          placeholder="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        <button type="submit">{title}</button>
      </form>
      <Link to={`${oppositeAction}`}>{oppositeTitle}</Link>
    </>
  );
};

export default AccountForm;
