import React, { useState } from "react";
import { Link } from "react-router-dom";

const API_ROOT = `https://strangers-things.herokuapp.com/api/2108-LSU-RM-WEB-PT/users`;
const API_REGISTER = `${API_ROOT}register`;
const API_LOGIN = `${API_ROOT}login`;
const API_USER = `${API_ROOT}me`;

const AccountForm = ({ action, setToken }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const isLogin = action == "login";
  const title = isLogin ? "Login" : "Register";
  const oppositeTittle = isLogin ? "Register" : "Login";
  const oppositeAction = isLogin ? "register" : "login";
  const actionURL = isLogin ? API_LOGIN : API_REGISTER;

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log(`user name: ${username}`);
    console.log(`password: ${password}`);
    try {
      const response = await fetch(actionURL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user: {
            username: username,
            password: password,
          },
        }),
      });

      const { data } = await response.json();
      // console.log(data);
      const token = data.token;
      //console.log(token);
      if (token) {
        setToken(data);
        const userResponse = await fetch(API_USER, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}` ,
          },
        });
      }
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <>
      <h4>{title}</h4>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="username"
          value={username}
          onChange={(event) => setUsername(event.target.value)}
        ></input>
        <input
          type="password"
          placeholder="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        <button type="submit"> {title}</button>
      </form>
      <Link to={`${oppositeAction}`}>{oppositeTittle}</Link>
    </>
  );
};

export default AccountForm;
